var SAElFacebookFeed = function($scope, $) {

    if (!isEditMode) {
        var $facebook_gallery = $(".sa-el-facebook-feed", $scope).isotope({
            itemSelector: ".sa-el-facebook-feed-item",
            percentPosition: true,
            columnWidth: ".sa-el-facebook-feed-item"
        });

        $facebook_gallery.imagesLoaded().progress(function() {
            $facebook_gallery.isotope("layout");
        });
    }

    $(".sa-el-load-more-facebook-feed", $scope).on("click", function(e) {
        e.preventDefault();
        var $this = $(this);
        var $settings = $this.attr("data-settings");
        var $page = parseInt($this.data("page")) + 1;
        var $class = $this.attr("data-class");
        var $args = '[]';
        var $function = $this.attr("data-function");
        $this.addClass("button--loading");
        $("span", $this).html("Loading...");
        $.ajax({
            url: sa_el_addons_loader.ajaxurl,
            type: "post",
            data: {
                action: "sa_el_addons_loader",
                _wpnonce: sa_el_addons_loader.nonce,
                class: $class,
                function: $function,
                args: $args,
                settings: $settings,
                optional: $page
            },
            success: function(response) {
                response = $.parseJSON(response);
                $this.data("page", $page);

                var $html = $(response.html).filter("div");
                $facebook_gallery = $(".sa-el-facebook-feed", $scope).isotope();
                $(".sa-el-facebook-feed", $scope).append($html);
                $facebook_gallery.isotope("appended", $html);
                $facebook_gallery.imagesLoaded().progress(function() {
                    $facebook_gallery.isotope("layout");
                });
                if (response.pages > $page + 1) {
                    $this.removeClass("button--loading");
                    $("span", $this).html("Load more");
                } else {
                    $this.remove();
                }
            },
            error: function() {}
        });
    });
};
jQuery(window).on("elementor/frontend/init", function() {
    elementorFrontend.hooks.addAction(
        "frontend/element_ready/sa_el_facebook_feed.default",
        SAElFacebookFeed
    );
});