var SaElHelpDesk = function($scope, $) {

    var $helpdesk = $scope.find('.sa-el-helpdesk'),
        $helpdeskTooltip = $helpdesk.find('.sa-el-helpdesk-icons');

    if (!$helpdesk.length) {
        return;
    }
    SaEltippyTooltip($helpdeskTooltip, $scope);

};
var SaEltippyTooltip = function($scope, $appendIn) {
    var $tooltip = $scope.find('.sa-el-tippy-tooltip');

    $tooltip.each(function(index) {
        tippy(this, {
            appendTo: $appendIn[0]
        });
    });
};


jQuery(window).on("elementor/frontend/init", function() {
    elementorFrontend.hooks.addAction(
        "frontend/element_ready/sa_el_help_desk.default",
        SaElHelpDesk
    );
});