var SaElIconNav = function($scope, $) {


    var $iconnav = $scope.find('div.sa-el-icon-nav'),
        $iconnavTooltip = $iconnav.find('.sa-el-icon-nav');

    if (!$iconnav.length) {
        return;
    }

    window.SaeltippyTooltip($iconnavTooltip, $scope);

};
var SaelOffcanvas = function($scope, $) {

    var $offcanvas = $scope.find('.sa-el-offcanvas');

    if (!$offcanvas.length) {
        return;
    }


    $.each($offcanvas, function(index, val) {

        var $this = $(this),
            $settings = $this.data('settings'),
            offcanvasID = $settings['id'];

        if ($(offcanvasID).length) {
            $(offcanvasID).on("click", function(event) {
                event.preventDefault();
                window.offcanvas($this).show();
            });
        }

    });




};

var SaeltippyTooltip = function($selector, $appendIn) {
    var $tooltip = $selector.find('> .sa-el-tippy-tooltip');

    $tooltip.each(function(index) {
        tippy(this, {
            appendTo: $appendIn[0]
        });
    });

};
jQuery(window).on("elementor/frontend/init", function() {

    elementorFrontend.hooks.addAction(
        "frontend/element_ready/sa_el_icon_nav.default",
        SaElIconNav
    );
});