
var SAwidgetIframe = function ($scope, $) {


    var $iframe = $scope.find('.sa-el-iframe > iframe'),
            $autoHeight = $iframe.data('auto_height');

    if (!$iframe.length) {
        return;
    }

    if ($autoHeight) {
        $($iframe).load(function () {
            $(this).height($(this).contents().find("html").height());
        });
    }

    lazyLoader($iframe);


   function lazyLoader($scope) {
        var $lazyload = $scope;

        $($lazyload).recliner({
            throttle: $lazyload.data('throttle'),
            threshold: $lazyload.data('threshold'),
            live: $lazyload.data('live')
        });

    }
}
;
jQuery(window).on("elementor/frontend/init", function () {
    elementorFrontend.hooks.addAction(
            "frontend/element_ready/sa-el-iframe.default",
            SAwidgetIframe
            );
});