var SaElQRCode = function($scope, $) {
    var $qrcode = $scope.find('.sa-el-qrcode'),
        image = $scope.find('.sa-el-qrcode-image');

    if (!$qrcode.length) {
        return;
    }
    var settings = $qrcode.data('settings');
    settings.image = image[0];

    $($qrcode).qrcode(settings);
};


jQuery(window).on("elementor/frontend/init", function() {
    elementorFrontend.hooks.addAction(
        "frontend/element_ready/sa_el_qr_code.default",
        SaElQRCode
    );
});