var SaElScrollButton = function($scope, $) {
    var $scrollButton = $scope.find('.sa-el-scroll-button'),
        $selector = $scrollButton.data('selector'),
        $settings = $scrollButton.data('settings');

    if (!$scrollButton.length) {
        return;
    }

    $($scrollButton).on('click', function(event) {
        event.preventDefault();
        $('body').scroll($scrollButton, $settings).scrollTo($($selector));

    });
};

jQuery(window).on("elementor/frontend/init", function() {
    elementorFrontend.hooks.addAction(
        "frontend/element_ready/sa_el_scroll_button.default",
        SaElScrollButton
    );
});