var SAElTwitterFeed = function($scope, $) {

    if (!isEditMode) {
        $gutter = $(".sa-el-twitter-feed-masonry", $scope).data("gutter");
        $settings = {
            itemSelector: ".sa-el-twitter-feed-item",
            percentPosition: true,
            masonry: {
                columnWidth: ".sa-el-twitter-feed-item",
                gutter: $gutter
            }
        };
        $twitter_feed_gallery = $(".sa-el-twitter-feed-masonry", $scope).isotope(
            $settings
        );
        $twitter_feed_gallery.imagesLoaded().progress(function() {
            $twitter_feed_gallery.isotope("layout");
        });
    }
};
jQuery(window).on("elementor/frontend/init", function() {
    elementorFrontend.hooks.addAction(
        "frontend/element_ready/sa_el_twitter_feed.default",
        SAElTwitterFeed
    );
});