var SAWhatsup = function ($scope, $) {
    var SAWhatsChat = $scope.find(".sa-el-whatsapp-container"),
            SAWhatsChatSettings = SAWhatsChat.data("settings");

    if (SAWhatsChatSettings["hideMobile"]) {
        if ($(window).outerWidth() < SAWhatsChatSettings["mob"]) {
            $(SAWhatsChat).css("display", "none");
        }
    } else if (SAWhatsChatSettings["hideTab"]) {
        if (
                $(window).outerWidth() > SAWhatsChatSettings["mob"] &&
                $(window).outerWidth() < SAWhatsChatSettings["tab"]
                ) {
            $(SAWhatsChat).css("display", "none");
        }
    }

    if (SAWhatsChatSettings["tooltips"]) {
        SAWhatsChat.find(".sa-el-whatsapp-link").tooltipster({
            functionInit: function (instance, helper) {
                var content = $(helper.origin)
                        .find("#tooltip_content")
                        .detach();
                instance.content(content);
            },
            functionReady: function () {
                $(".tooltipster-box").addClass(
                        "tooltipster-box-" + SAWhatsChatSettings["id"]
                        );
            },
            animation: SAWhatsChatSettings["anim"],
            contentCloning: true,
            trigger: "hover",
            arrow: true,
            contentAsHTML: true,
            autoClose: false,
            minIntersection: 16,
            interactive: true,
            delay: 0,
            side: ["right", "left", "top", "bottom"]
        });
    }
};
jQuery(window).on("elementor/frontend/init", function () {
    elementorFrontend.hooks.addAction(
            "frontend/element_ready/sa_el_whatsapp_chat.default",
            SAWhatsup
            );
});
